            this.Items.Add(new ItemViewModel() { ID = "233", LineOne = "Lend Me Your Comb", LineTwo = "Anthology", Lyrics = "Lend me your comb\nIt's time to go home\nI got to go past\nMy hair is a mess\n\nYour mammie will scold\nYour pappie will shout\nUnless we come in\nThe way we went out\n\nKissing you was fun honey\nBut thanks for the date\nBut I must come to run honey\nBut you know baby it's getting late\n\nJust wait till I say\n\"My darling\nLend me your comb\nWe got to go home\"\n\nWhoa! Ow!\n\nKissing you was fun honey\nBut thanks for the date\nBut I must come to run, honey\nBut sugar, it's getting late\n\nJust wait till I say\n\"My darling\nLend me your comb\nWe got to go home\"",Trivia = "It was originally the B-Side of Carl Perkins' \"Glad All Over\" single, released December 1957 on Sun Records as Sun 287 by \"The Rockin' Guitar Man\". This was Carl Perkins' last single on Sun Records. The vocals were by Carl Perkins and Jay Perkins. Today it is best known for a cover by The Beatles, recorded 2 July 1963 at the BBC Maida Vale Studios in London. The Beatles cover has helped the song gain some notability. Musically, it is best known as a Rock'n'Roll song, and is usually performed as such. The Beatles version was released on their Anthology 1 album.\nIt also appeared on the Beatles' Live! at the Star-Club in Hamburg, Germany; 1962 album.",Personnel = "Not available", Image1 = "http://upload.wikimedia.org/wikipedia/en/thumb/f/f8/Anthology1cover.jpg/220px-Anthology1cover.jpg" ,AudioSrc = "https://www.dropbox.com/s/bn8nokuocyr2gms/27%20-%20Lend%20Me%20Your%20Comb.mp3" });
